mapper.select:
select DATE_FORMAT(a.createTime,'%Y-%m-%d %H:%i:%s') as createTime, a.*,c.categoryName,c.aliasName from t_article a inner join t_category c on a.categoryId = c.id inner join t_article_tag t on a.id = t.articleId inner join t_tag ta on t.tagId = ta.id where 1 = 1 and a.statue = 0
and ta.id = #{tagId}
and a.id < #{articleId}
order by a.id desc

select * from t_tag where 1 = 1
and tagName like concat('%', #{tag.tagName}, '%')
order by id desc limit #{start},#{limit}

mapper.update:
update t_tag   
	set tagName = #{tagName} 
where id = #{id}

mapper.select:
select ta.* from t_article_tag aat 
inner join t_tag ta on aat.tagId = ta.id 
where aat.articleId = #{id}
